import React from "react";
import keyLogo from './key2.svg'
import deleteLogo from './delete.svg'
import '../../keys.css'
import AudienceNumber from "./audienceNumber";
import KeyStatus from "./status";


class KeyCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isHovered: false
        };
    }

    handleMouseEnter = () => {
        this.setState({ isHovered: true });
    }

    handleMouseLeave = () => {
        this.setState({ isHovered: false });
    }

    handleDelete = (event) => {
        event.preventDefault();

        const name = document.getElementById('inputDeaa').value;

        const requestBody = {
            "name": name
        };

        //пока токена нет
        //let token = "eyJhbGciOiJIUzI1NiJ9.eyJqdGkiOiIzYjFkYTk3YS04MTI0LTQyN2YtOGUwZC04MGQxODQyMjk4M2UiLCJzdWIiOiJrc3U0IiwiaWF0IjoxNzEwMDk0MDQ4LCJleHAiOjE3MTAwOTc2NDh9.eeOB92ZUATUPV4iy66M2mGejm7fwdVwFjgDWJF9wnF0";
        //localStorage.setItem("token", token);
        //

        try {
            const response = fetch("http://158.160.147.51:8181/api/office/create", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + localStorage.getItem('token')
                },
                body: JSON.stringify(requestBody)
            }).then(response => {
                if (response.status === 200) {
                    return response.json();
                } else {
                    console.log(response);
                    throw new Error();
                }

            }).then(response => {
                console.log(response);
                return response.data;
            }).catch(error => {
                console.log('Ошибка:', error);
            });

        } catch (error) {
            console.log('Ошибка:', error);
        }
    };

    render() {
        const { officeNumber, userViewDTO } = this.props;
        return (
            <div className="keyCard" onMouseEnter={this.handleMouseEnter} onMouseLeave={this.handleMouseLeave}>
                <img className="keyImage" src={keyLogo} alt="" />
                <span className="keyCard2">
                    <AudienceNumber officeNumber={officeNumber} />
                    <KeyStatus userViewDTO={userViewDTO} />
                </span>
                {this.state.isHovered && <img className="deleteImage" src={deleteLogo} alt="" onClick={this.handleDelete} />}
            </div>
        );
    }
}

export default KeyCard;
